mapper.select:
SELECT * FROM ( select b.id id,p.`name` as product_name,p.property_id, b.open_balance as balance, b.open_time openTime, b.interest_begin_date interestBeginDate, b.status, b.product_rate product_rate, p.id productId, p.term term,p.property_type propertyType, case when p.term = 12 then (365 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) when p.term = -7 then (7 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) else (p.term * 30 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) end investDay, b.last_finish_interest_date investEndTime, c.amount AS red_amount, p.start_time,0 AS ticket_apr,0 AS interest_amount from bs_sub_account b ,bs_account a,bs_product p, bs_pay_orders o LEFT JOIN bs_red_packet_info c ON o.order_no = c.order_no AND c.`status` = 'USED'
b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6
and b.product_type IN ('REG')
and (o.trans_type = 'CARD_BUY_PRODUCT' OR o.trans_type = 'BALANCE_BUY_PRODUCT') AND a.user_id=#{userId , jdbcType=INTEGER}
and ( b.status = #{status} or b.status = 7 )
AND o.sub_account_id = b.id
UNION
select b.id id,p.`name` as product_name,p.property_id, b.open_balance + IFNULL(red.open_balance,0) as balance, b.open_time openTime, b.interest_begin_date interestBeginDate, b.status, b.product_rate product_rate, p.id productId, p.term term,p.property_type propertyType, case when p.term = 12 then (365 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) when p.term = -7 then (7 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) else (p.term * 30 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) end investDay, b.last_finish_interest_date investEndTime, red.open_balance AS red_amount, p.start_time, ifnull(ti.ticket_apr, '0') AS ticket_apr, ifnull(ti.interest_amount, '0') AS interest_amount from bs_sub_account b LEFT JOIN bs_sub_account_pair pr ON pr.auth_account_id = b.id LEFT JOIN bs_sub_account red ON red.id = pr.red_account_id LEFT JOIN bs_interest_ticket_info ti ON b.id = ti.auth_account_id ,bs_account a,bs_product p
b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6
and b.product_type IN ('AUTH_YUN','AUTH_ZSD','AUTH_7', 'AUTH_FREE')
AND a.user_id=#{userId , jdbcType=INTEGER}
and ( b.status = #{status} or b.status = 7 )
) aa order by aa.investEndTime desc , aa.openTime desc, aa.id desc limit #{start , jdbcType=INTEGER}, #{pageSize , jdbcType=INTEGER}

