			 	and s5.id not in  #{item}  
			    and s5.status in (2,5,7) and s5.interest_begin_date = DATE_ADD(DATE_FORMAT(#{time},'%Y-%m-%d'), INTERVAL 1 day) 
			 group by s5.account_id 
		) a
	) as investUserCount,
	(select IFNULL(SUM(s3.open_balance),0) from bs_sub_account s3 
