	AND a.balance >= #{lessAmount} 


	AND a.balance >= #{lessAmount} AND a.balance < #{moreAmount}

AND b.user_id = #{userId}

SELECT * FROM ( SELECT c.mobile, c.user_name, d.term, d.base_rate, a.balance, CASE d.term WHEN 1 THEN 30 WHEN 3 THEN 90 WHEN 6 THEN 180 WHEN 12 THEN 365 END AS need_interest_days, CASE a.`status` WHEN 2 THEN '' WHEN 5 THEN '' WHEN 7 THEN '' END AS `status`, a.open_time, a.interest_begin_date, a.last_finish_interest_date AS return_money_days, e.agent_name, ( CASE WHEN a.`product_code` IN ('1413','1411','1412','1414') THEN CASE d.term WHEN 1 THEN a.balance*30/365*0.2 WHEN 3 THEN a.balance*90/365*0.2 WHEN 6 THEN a.balance*180/365*0.2 WHEN 12 THEN a.balance*0.2 END WHEN SUBSTR(a.`product_code`,1,2) IN('24') THEN CASE d.term WHEN 1 THEN a.balance*30/365*0.18 WHEN 3 THEN a.balance*90/365*0.18 WHEN 6 THEN a.balance*180/365*0.18 WHEN 12 THEN a.balance*0.18 END ELSE CASE d.term WHEN 1 THEN a.balance*30/365*0.24 WHEN 3 THEN a.balance*90/365*0.24 WHEN 6 THEN a.balance*180/365*0.24 WHEN 12 THEN a.balance*0.24 END END
) AS dafy_return_balance, ( CASE d.term WHEN 1 THEN a.balance*30/365*(d.base_rate/100) WHEN 3 THEN a.balance*90/365*(d.base_rate/100) WHEN 6 THEN a.balance*180/365*(d.base_rate/100) WHEN 12 THEN a.balance*(d.base_rate/100) END ) AS user_interest_balance, ( CASE d.term WHEN 1 THEN CASE WHEN agent_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE2')*a.balance/100 END END WHEN 3 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_3MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE')*a.balance/100 END END WHEN 6 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_6MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE3')*a.balance/100 END END WHEN 12 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1YEAR')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE4')*a.balance/100 END END END ) AS user_bonus_balance FROM bs_sub_account a, bs_account b, bs_user c LEFT JOIN bs_agent e ON e.id = c.agent_id, bs_product d WHERE a.account_id = b.id AND b.user_id = c.id AND a.product_type = 'REG' AND a.`status` IN(2,5,7) AND d.id = a.product_id AND a.interest_begin_date BETWEEN '2015-12-2 00:00:00' AND '2016-2-1 23:59:59' ORDER BY return_money_days ASC ) AS aaa
WHERE DATE(aaa.return_money_days) = #{startTime}
WHERE DATE(aaa.return_money_days) >= #{startTime} AND DATE(aaa.return_money_days) < #{endTime}
WHERE YEAR(aaa.return_money_days) = #{startTime}
LIMIT #{start}, #{numPerPage}

SELECT COUNT(*) FROM ( SELECT c.mobile, c.user_name, d.term, d.base_rate, a.balance, CASE d.term WHEN 1 THEN 30 WHEN 3 THEN 90 WHEN 6 THEN 180 WHEN 12 THEN 365 END AS need_interest_days, CASE a.`status` WHEN 2 THEN '' WHEN 5 THEN '' WHEN 7 THEN '' END AS `status`, a.open_time, a.interest_begin_date, a.last_finish_interest_date AS return_money_days, e.agent_name, ( CASE d.term WHEN 1 THEN a.balance*30/365*0.24 WHEN 3 THEN a.balance*90/365*0.24 WHEN 6 THEN a.balance*180/365*0.24 WHEN 12 THEN a.balance*0.24 END ) AS dafy_return_balance, ( CASE d.term WHEN 1 THEN a.balance*30/365*(d.base_rate/100) WHEN 3 THEN a.balance*90/365*(d.base_rate/100) WHEN 6 THEN a.balance*180/365*(d.base_rate/100) WHEN 12 THEN a.balance*(d.base_rate/100) END ) AS user_interest_balance, ( CASE d.term WHEN 1 THEN CASE WHEN agent_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE2')*a.balance/100 END END WHEN 3 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_3MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE')*a.balance/100 END END WHEN 6 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_6MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE3')*a.balance/100 END END WHEN 12 THEN CASE WHEN recommend_id = 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1YEAR')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE4')*a.balance/100 END END END ) AS user_bonus_balance FROM bs_sub_account a, bs_account b, bs_user c LEFT JOIN bs_agent e ON e.id = c.agent_id, bs_product d WHERE a.account_id = b.id AND b.user_id = c.id AND a.product_type = 'REG' AND a.`status` IN(2,5,7) AND d.id = a.product_id AND a.interest_begin_date BETWEEN '2015-12-2 00:00:00' AND '2016-2-1 23:59:59' ORDER BY return_money_days ASC ) AS count_query
WHERE DATE(count_query.return_money_days) = #{startTime}
WHERE DATE(count_query.return_money_days) >= #{startTime} AND DATE(count_query.return_money_days) < #{endTime}
WHERE YEAR(count_query.return_money_days) = #{startTime}

SELECT * FROM ( SELECT a.id AS sub_account_id, c.id AS user_id, c.mobile, c.user_name, d.term, CASE WHEN d.term = 12 THEN 365 WHEN d.term > 0 AND d.term != 12 THEN d.term*30 WHEN d.term < 0 THEN -d.term END AS interest_days, d.base_rate, a.balance, CASE a.`status` WHEN 2 THEN '' WHEN 5 THEN '' WHEN 7 THEN '' END AS `status`, a.open_time, a.interest_begin_date, a.last_finish_interest_date, e.agent_name, CASE WHEN d.`code` IN ('1413','1411','1412','1414') AND h.property_symbol = 'YUN_DAI' THEN CASE d.term WHEN 1 THEN a.balance * 0.2 * 30 / 365 WHEN 3 THEN a.balance * 0.2 * 90 / 365 WHEN 6 THEN a.balance * 0.2 * 180 / 365 WHEN 12 THEN a.balance * 0.2 WHEN -7 THEN a.balance * 0.20 * 7 / 365 END WHEN SUBSTR(d.`code`,1,2) IN('24') AND h.property_symbol = '7_DAI' THEN CASE d.term WHEN 1 THEN a.balance * 0.18 * 30 / 365 WHEN 3 THEN a.balance * 0.18 * 90 / 365 WHEN 6 THEN a.balance * 0.18 * 180 / 365 WHEN 12 THEN a.balance * 0.18 WHEN -7 THEN a.balance * 0.20 * 7 / 365 END ELSE CASE d.term WHEN 1 THEN a.balance * 0.24 * 30 / 365 WHEN 3 THEN a.balance * 0.24 * 90 / 365 WHEN 6 THEN a.balance * 0.24 * 180 / 365 WHEN 12 THEN a.balance * 0.24 WHEN -7 THEN a.balance * 0.20 * 7 / 365 END END AS total_interest_amount, CASE WHEN d.term > 0 AND d.term != 12 THEN a.balance * d.base_rate * d.term * 30 / 365 / 100 WHEN d.term = 12 THEN a.balance * d.base_rate / 100 WHEN d.term < 0 THEN -d.term * a.balance * d.base_rate / 365 / 100
END AS user_interest_amount, CASE d.term WHEN 1 THEN CASE WHEN f.grant_type = 'RECOMMEND_USER_TAKE_ALL' THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE2')*a.balance/100 ELSE CASE agent_id WHEN 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE2')*a.balance/100 END END END WHEN 3 THEN CASE WHEN f.grant_type = 'RECOMMEND_USER_TAKE_ALL' THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE')*a.balance/100 ELSE CASE agent_id WHEN 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_3MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE')*a.balance/100 END END END WHEN 6 THEN CASE WHEN f.grant_type = 'RECOMMEND_USER_TAKE_ALL' THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE3')*a.balance/100 ELSE CASE agent_id WHEN 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_6MONTH')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE3')*a.balance/100 END END END WHEN 12 THEN CASE WHEN f.grant_type = 'RECOMMEND_USER_TAKE_ALL' THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE4')*a.balance/100 ELSE CASE agent_id WHEN 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_1YEAR')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE4')*a.balance/100 END END END WHEN -7 THEN CASE WHEN f.grant_type = 'RECOMMEND_USER_TAKE_ALL' THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE_7DAY')*a.balance/100 ELSE CASE agent_id WHEN 31 THEN (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'BONUS_RATE_4_SELF_7DAY')*a.balance/100 ELSE CASE WHEN recommend_id IS NULL THEN 0 ELSE (SELECT config.conf_value FROM bs_sys_config config WHERE config.conf_key = 'PUSH_MONEY_RATE_7DAY')*a.balance/100 END END END END AS user_bonus_balance FROM bs_account b, bs_user c LEFT JOIN bs_agent e ON e.id = c.agent_id, bs_product d LEFT JOIN bs_property_info h ON d.property_id = h.id, bs_sub_account a LEFT JOIN bs_bonus_grant_plan f ON f.sub_account_id = a.id WHERE a.product_type = 'REG' AND a.`status` IN(2,5,7) AND a.account_id = b.id AND b.user_id = c.id AND a.product_id = d.id ) AS aaa WHERE 1=1
and DATE(aaa.last_finish_interest_date) >= #{startTime}
and DATE(aaa.last_finish_interest_date) <= #{endTime}
LIMIT #{start}, #{numPerPage}

