SELECT d.id AS agent_id, d.agent_name, e.term, (a.open_balance + ifnull(g.open_balance, 0)) as balance, ROUND(CASE WHEN e.term > 0 AND e.term != 12 THEN (a.open_balance + ifnull(g.open_balance, 0)) * e.term * 30 / 365 WHEN e.term = 12 THEN (a.open_balance + ifnull(g.open_balance, 0)) * 365 / 365 WHEN e.term < 0 THEN -e.term * (a.open_balance + ifnull(g.open_balance, 0)) / 365 END, 2) AS cp, e.`name` AS product_name, e.base_rate FROM bs_sub_account a LEFT JOIN bs_sub_account_pair f ON a.id = f.auth_account_id LEFT JOIN bs_sub_account g ON f.red_account_id = g.id , bs_account b, bs_user c LEFT JOIN bs_agent d ON d.id = c.agent_id, bs_product e WHERE a.account_id = b.id AND b.user_id = c.id AND a.product_id = e.id AND a.product_type IN ('REG', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7', 'AUTH_FREE') AND a.`status` NOT IN(1,6)
AND DATE(a.open_time) BETWEEN DATE(#{startTime}) AND DATE(#{endTime})
AND c.agent_id = #{agentId}
LIMIT #{start}, #{numPerPage}

