select t.*,rp.amount red_amount from (select b.id id, p.name product_name, b.balance balance, b.open_time open_time, b.interest_begin_date interest_begin_date, b.status status, b.product_rate product_rate, p.id product_id, p.term term, p.start_time start_time, case when term = 12 then (365 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) when term = -7 then (7 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) else (term * 30 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) end investDay, b.last_finish_interest_date investEndTime, o.order_no order_no, p.property_type property_type from bs_sub_account b,bs_account a,bs_product p,bs_pay_orders o where b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6 and o.sub_account_id = b.id and b.product_type='REG' and (o.trans_type = 'CARD_BUY_PRODUCT' OR o.trans_type = 'BALANCE_BUY_PRODUCT') and a.user_id=#{userId , jdbcType=INTEGER}
and b.status = #{status}
) t left join (select r.order_no,r.amount from bs_red_packet_info r where r.status = 'USED') rp on t.order_no = rp.order_no order by t.investEndTime desc , t.open_time desc, t.id desc limit #{start , jdbcType=INTEGER}, #{pageSize , jdbcType=INTEGER}

