	from bs_sub_account a,bs_product b,
		bs_red_packet_info c,bs_red_packet_check d,bs_account e,bs_pay_orders f
	where a.id=#{subAccountId} and e.user_id = #{userId} and e.user_id = c.user_id 
		and e.user_id = f.user_id and a.id = f.sub_account_id 
		and f.order_no = c.order_no and c.serial_no = d.serial_no and a.product_id = b.id;

select tt.*,case when tt.rowno>3 then ROUND(tt.buy_amount*0.003,2) else ROUND(tt.buy_amount*0.005,2) end as award_amount from( SELECT (@rowNO := @rowNo + 1) rowno, t.* FROM (SELECT @rowNO := 0) b, ( select bu.id,CONCAT(LEFT(bu.user_name,1),"**") user_name, ROUND(sum(sub.year_invest),2) buy_amount FROM (select a.account_id,a.open_balance,b.term,a.product_type,a.open_time, CASE WHEN a.product_type = 'REG' THEN CASE WHEN b.term <0 then abs(b.term) * a.open_balance /365 ELSE case when b.term = 12 then a.open_balance ELSE b.term*30*a.open_balance/365 END END ELSE b.term*a.open_balance/24 END as year_invest from bs_sub_account a,bs_product b where a.product_id = b.id and a.`status` =2 and a.interest_begin_date = #{selectDate} and (a.product_type='AUTH' or a.product_type='REG') and (b.name like '%' or b.name like '%') ) sub,bs_account ba,bs_user bu where sub.account_id = ba.id and ba.user_id = bu.id
and ba.user_id = #{userId}
GROUP BY bu.id ORDER BY buy_amount DESC,sub.open_time ASC ) t )tt where buy_amount>=3000 limit 0,10

SELECT aa.time, (aa.amount-IFNULL(bb.amount,0)) AS amount FROM ( SELECT IFNULL(sum(a.balance),0) AS amount, DATE(a.open_time) AS time FROM bs_sub_account a WHERE a.product_type = 'REG' AND a.status NOT IN (1,6) AND DATE(a.open_time) BETWEEN #{startTime} AND #{endTime} GROUP BY DATE(a.open_time)
) aa LEFT JOIN ( SELECT IFNULL(sum(a.balance),0) AS amount, DATE(a.open_time) AS time FROM bs_sub_account a, bs_product b, bs_property_info c WHERE a.product_id = b.id AND c.id = b.property_id AND c.property_symbol = '7_DAI' AND a.product_type = 'REG' AND a.status NOT IN (1,6) AND DATE(a.open_time) BETWEEN #{startTime} AND #{endTime} GROUP BY DATE(a.open_time)
) bb ON aa.time = bb.time ORDER BY aa.time DESC

SELECT COUNT(bb.time) FROM (
    SELECT SUM(aa.amount) AS amount, aa.time FROM (
            (
                    SELECT IFNULL(SUM(a.principal), 0) AS amount, DATE(a.update_time) AS time
                    FROM bs_loan_finance_repay a LEFT JOIN bs_sys_config b ON b.conf_key = 'SUPER_FINANCE_USER_ID'
                    WHERE a.status = 'REPAIED' AND !FIND_IN_SET(a.fn_user_id, b.conf_value)
                        AND DATE(a.update_time) BETWEEN #{startTime} AND #{endTime}
                    GROUP BY DATE(a.update_time)
            ) UNION (
                    SELECT SUM((a.before_balance1 - a.after_balance1)) AS amount, DATE(a.trans_time) AS time
                            FROM bs_account_jnl a, bs_sub_account b, bs_account c LEFT JOIN bs_sys_config d ON d.conf_key = 'SUPER_FINANCE_USER_ID'
                            WHERE a.trans_code = 'AUTH_BACK' AND a.sub_account_id1 = b.id AND b.account_id = c.id AND !FIND_IN_SET(c.user_id, d.conf_value)
                                AND DATE(a.trans_time) BETWEEN #{startTime} AND #{endTime}
                    GROUP BY DATE(a.trans_time)
            )
    ) AS aa
    GROUP BY aa.time
) AS bb

SELECT SUM(aa.amount) AS amount FROM (
		(
				SELECT IFNULL(SUM(a.principal), 0) AS amount, DATE(a.update_time) AS time
				FROM bs_loan_finance_repay a LEFT JOIN bs_sys_config b ON b.conf_key = 'SUPER_FINANCE_USER_ID'
				WHERE a.status = 'REPAIED' AND !FIND_IN_SET(a.fn_user_id, b.conf_value)
					AND DATE(a.update_time) BETWEEN #{startTime} AND #{endTime}
				GROUP BY DATE(a.update_time)
		) UNION (
				SELECT SUM((a.before_balance1 - a.after_balance1)) AS amount, DATE(a.trans_time) AS time
						FROM bs_account_jnl a, bs_sub_account b, bs_account c LEFT JOIN bs_sys_config d ON d.conf_key = 'SUPER_FINANCE_USER_ID'
						WHERE a.trans_code = 'AUTH_BACK' AND a.sub_account_id1 = b.id AND b.account_id = c.id AND !FIND_IN_SET(c.user_id, d.conf_value)
							AND DATE(a.trans_time) BETWEEN #{startTime} AND #{endTime}
				GROUP BY DATE(a.trans_time)
		)
) AS aa

select COUNT(*) from bs_sub_account b,bs_account a,bs_product p where b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6 and b.product_type in ('REG', 'AUTH_YUN', 'AUTH_ZSD','AUTH_7','AUTH_FREE') AND a.user_id= #{userId , jdbcType=INTEGER}
AND (b.status = 2 OR b.status = 7 )
AND b.status = 5

SELECT *, CASE WHEN aa.`status` = 7 THEN 'RETURING' WHEN aa.`status` = 5 THEN 'FINISH' WHEN aa.`status` = 2 AND aa.investDay <= 7 THEN 'EXPIRE' WHEN aa.`status` = 2 AND aa.investDay > 7 THEN 'HOLDING' END order_status FROM ( select b.id id,p.`name` as product_name,p.property_id, IFNULL(b.open_balance, 0) AS balance, b.open_time openTime, b.interest_begin_date interestBeginDate, b.status, b.product_rate product_rate, p.id productId, p.term term,p.property_type propertyType,b.product_type AS productType, case when p.term = 12 then (365 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) when p.term = -7 then (7 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) else (p.term * 30 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) end investDay, b.last_finish_interest_date investEndTime, c.amount AS red_amount, p.start_time,0 AS ticket_apr,0 AS interest_amount from bs_sub_account b ,bs_account a,bs_product p, bs_pay_orders o LEFT JOIN bs_red_packet_info c ON o.order_no = c.order_no AND c.`status` = 'USED'
b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6 and
b.product_type in ('REG')
b.product_type=#{type}
and (o.trans_type = 'CARD_BUY_PRODUCT' OR o.trans_type = 'BALANCE_BUY_PRODUCT') AND a.user_id=#{userId , jdbcType=INTEGER}
AND (b.status = 2 OR b.status = 7 )
AND b.status = 5
AND o.sub_account_id = b.id
UNION
select b.id id,p.`name` as product_name,p.property_id, IFNULL(b.open_balance, 0)+IFNULL(red.open_balance, 0) AS balance, b.open_time openTime, b.interest_begin_date interestBeginDate, b.status, b.product_rate product_rate, p.id productId, p.term term,p.property_type propertyType,b.product_type AS productType, case when p.term = 12 then (365 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) when p.term = -7 then (7 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) else (p.term * 30 - case when datediff(now(), b.interest_begin_date) < 0 then 0 else datediff(now(), b.interest_begin_date) end ) end investDay, b.last_finish_interest_date investEndTime, red.open_balance AS red_amount, p.start_time, ifnull(ti.ticket_apr,'0') AS ticket_apr, ifnull(ti.interest_amount,'0') AS interest_amount from bs_sub_account b LEFT JOIN bs_sub_account_pair pair ON pair.auth_account_id = b.id LEFT JOIN bs_sub_account red ON red.id = pair.red_account_id LEFT JOIN bs_interest_ticket_info ti ON b.id = ti.auth_account_id ,bs_account a,bs_product p
b.account_id = a.id and b.product_id = p.id and b.status != 1 and b.status != 6 and
b.product_type in ('AUTH_YUN', 'AUTH_ZSD','AUTH_7','AUTH_FREE')
b.product_type=#{type}
AND a.user_id=#{userId , jdbcType=INTEGER}
AND (b.status = 2 OR b.status = 7 )
AND b.status = 5
) aa
order by field(order_status,'RETURING','EXPIRE','HOLDING','FINISH') , aa.interestBeginDate desc, aa.id desc
order by aa.investEndTime DESC , aa.id DESC
limit #{start , jdbcType=INTEGER}, #{pageSize , jdbcType=INTEGER}

select.if:
(SELECT DISTINCT * FROM ( SELECT a.id, a.account_id, a.product_id, a.product_type, a.open_balance, a.balance, a.available_balance, a.freeze_balance, a.`status`, a.interest_begin_date, bu.id user_id, bu.agent_id, hfu.hf_user_id, IFNULL(sred.available_balance, 0) AS red_available_balance, sred.id red_sub_account_id FROM bs_account ba INNER JOIN bs_user bu ON ba.user_id = bu.id INNER JOIN bs_hfbank_user_ext hfu ON hfu.user_id = bu.id INNER JOIN bs_sub_account a ON a.account_id = ba.id AND a.`status` = 2 AND a.product_type = #{productType} AND a.interest_begin_date <= DATE(NOW()) LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id AND b.`status` IN ('INIT', 'PASS', 'PROCESS') LEFT JOIN bs_sub_account_pair sp ON sp.auth_account_id = a.id LEFT JOIN bs_sub_account sred ON sred.id = sp.red_account_id AND sred.product_type = #{redProType} WHERE 1 = 1 AND b.id IS NULL
AND a.available_balance + IFNULL(sred.available_balance, 0) >= #{loanMatchRange.oneLevelMatchAmount}
and ba.user_id not in
#{userId}
ORDER BY (a.available_balance + IFNULL(sred.available_balance, 0)) DESC ) t1 limit #{loanMatchRange.redisLevelQueueCount})

(SELECT DISTINCT * FROM ( SELECT a.id, a.account_id, a.product_id, a.product_type, a.open_balance, a.balance, a.available_balance, a.freeze_balance, a.`status`, a.interest_begin_date, bu.id user_id, bu.agent_id, hfu.hf_user_id, IFNULL(sred.available_balance, 0) AS red_available_balance, sred.id red_sub_account_id FROM bs_account ba INNER JOIN bs_user bu ON ba.user_id = bu.id INNER JOIN bs_hfbank_user_ext hfu ON hfu.user_id = bu.id INNER JOIN bs_sub_account a ON a.account_id = ba.id AND a.`status` = 2 AND a.product_type = #{productType} AND a.interest_begin_date <= DATE(NOW()) LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id AND b.`status` IN ('INIT', 'PASS', 'PROCESS') LEFT JOIN bs_sub_account_pair sp ON sp.auth_account_id = a.id LEFT JOIN bs_sub_account sred ON sred.id = sp.red_account_id AND sred.product_type = #{redProType} WHERE 1 = 1 AND b.id IS NULL
AND a.available_balance + IFNULL(sred.available_balance, 0) >= #{loanMatchRange.twoLevelMatchAmount}
AND a.available_balance + IFNULL(sred.available_balance, 0) < #{loanMatchRange.oneLevelMatchAmount}
and ba.user_id not in
#{userId}
ORDER BY (a.available_balance + IFNULL(sred.available_balance, 0)) DESC ) t1 limit #{loanMatchRange.redisLevelQueueCount})

(SELECT DISTINCT * FROM ( SELECT a.id, a.account_id, a.product_id, a.product_type, a.open_balance, a.balance, a.available_balance, a.freeze_balance, a.`status`, a.interest_begin_date, bu.id user_id, bu.agent_id, hfu.hf_user_id, IFNULL(sred.available_balance, 0) AS red_available_balance, sred.id red_sub_account_id FROM bs_account ba INNER JOIN bs_user bu ON ba.user_id = bu.id INNER JOIN bs_hfbank_user_ext hfu ON hfu.user_id = bu.id INNER JOIN bs_sub_account a ON a.account_id = ba.id AND a.`status` = 2 AND a.product_type = #{productType} AND a.interest_begin_date <= DATE(NOW()) LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id AND b.`status` IN ('INIT', 'PASS', 'PROCESS') LEFT JOIN bs_sub_account_pair sp ON sp.auth_account_id = a.id LEFT JOIN bs_sub_account sred ON sred.id = sp.red_account_id AND sred.product_type = #{redProType} WHERE 1 = 1 AND b.id IS NULL
