select t.* from ( select a.id,a.account_id,a.product_id,a.product_type, a.open_balance,a.balance,a.available_balance,a.freeze_balance, a.`status`,a.interest_begin_date, bu.id user_id,bu.agent_id,hfu.hf_user_id, CASE WHEN sred.available_balance IS NULL THEN 0 ELSE CASE WHEN sred.available_balance >#{principal} THEN #{principal} ELSE sred.available_balance END END red_available_balance, CASE WHEN sred.available_balance IS NULL THEN a.available_balance ELSE CASE WHEN sred.available_balance >#{principal} THEN #{principal}+a.available_balance ELSE sred.available_balance+a.available_balance END END sumBalance, sred.id red_sub_account_id from bs_account ba, bs_user bu, bs_hfbank_user_ext hfu, bs_sub_account a LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id and b.`status` IN ('INIT', 'PASS','PROCESS') LEFT JOIN bs_sub_account_pair sp on sp.auth_account_id = a.id LEFT JOIN bs_sub_account sred on sred.id = sp.red_account_id and sred.product_type=#{redProType} where a.account_id = ba.id and ba.user_id =bu.id and a.product_type=#{productType} and b.id is null and a.available_balance>0 and a.interest_begin_date <= DATE(NOW()) and a.`status`=2 and hfu.user_id = bu.id
AND bu.id != #{outUserId}
AND (bu.agent_id IS NULL or bu.agent_id not in (select agent_id from bs_agent_view_config where agent_id!=49 GROUP BY agent_id) )
