SELECT TIMESTAMPDIFF(DAY,a.interest_begin_date,NOW()) invest_day, CASE WHEN e.id IS NULL THEN IFNULL(a.open_balance,0) ELSE IFNULL(a.open_balance,0) + IFNULL(e.open_balance,0) END open_balance, a.*, c.user_id, CASE WHEN b.term = 12 THEN 365 WHEN b.term = 24 THEN 730 END max_term FROM bs_sub_account a LEFT JOIN bs_sub_account_pair d ON a.id = d.auth_account_id LEFT JOIN bs_sub_account e ON d.red_account_id = e.id , bs_product b, bs_account c WHERE a.product_id = b.id AND a.account_id = c.id AND (a.product_type = 'AUTH_YUN' OR a.product_type = 'AUTH_ZSD' OR a.product_type = 'AUTH_7' OR a.product_type = 'AUTH_FREE') AND a.`status` IN ('2','7') AND ( CASE WHEN b.term = 12 THEN TIMESTAMPDIFF(DAY,a.interest_begin_date,NOW()) >= 365-1 WHEN b.term < 0 THEN TIMESTAMPDIFF(DAY,a.interest_begin_date,NOW()) >= -b.term-1 ELSE TIMESTAMPDIFF(DAY,a.interest_begin_date,NOW()) >= b.term * 30 -1 END )
AND c.user_id not in
#{userId}

