LEFT JOIN (
SELECT aa.p2,SUM(aa.total2) total2,aa.loan_id FROM ( select l.period as p2,SUM( CASE WHEN l.period = 1 AND DATEDIFF(NOW(), r.create_time) >= 10 THEN 0 ELSE r.total_amount END ) as total2,l.id loan_id from ln_loan_relation r, ln_loan l where r.loan_id=l.id and r.bs_user_id in
#{userId}
and r.`status`='SUCCESS' and r.total_amount=r.left_amount and r.left_amount>= #{matchLimitAmount} GROUP BY r.loan_id ) aa WHERE aa.loan_id NOT IN ( SELECT DISTINCT loan_id FROM ln_finance_repay_schedule s, ln_loan_relation b WHERE b.id = s.relation_id AND s. STATUS = 'REPAYING' AND b.bs_user_id IN
#{userId}
) GROUP BY aa.p2
) t2 on t1.period =t2.p2 LEFT JOIN ( select p.term, SUM( sa.balance) as user_total from bs_sub_account sa, bs_product p,bs_account sb where sa.product_type='AUTH' and sa.product_id=p.id and sa.account_id = sb.id and sb.user_id not in
#{userId}
and sa.balance>= #{matchLimitAmount} GROUP BY p.term) t3 on t1.period=t3.term
) t

    select count(t.id) from(
	    select sum(b1.open_balance),a1.mobile,MIN(b1.open_time) open_time,b1.id
		from bs_user a1,bs_sub_account b1,bs_account c1
		where a1.id = c1.user_id and c1.id = b1.account_id 
		and a1.agent_id =#{agentId} and b1.product_id = #{productId}
		and b1.product_type in ('REG', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7') and b1.`status` in (2,7) 
		GROUP BY a1.id 

	)t where 1=1

		and t.open_time >= #{createTimeBegin}


		and t.open_time <= #{createTimeEnd}

	;

    select * from(
	    select sum(b1.open_balance*100) ta_balance,a1.mobile user_account,MIN(b1.open_time) open_time
		from bs_user a1,bs_sub_account b1,bs_account c1
		where a1.id = c1.user_id and c1.id = b1.account_id 
		and a1.agent_id =#{agentId} and b1.product_id = #{productId}
		and b1.product_type in ('REG', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7') and b1.`status` in (2,7) 
		GROUP BY a1.id ORDER BY MIN(b1.open_time) desc

	)t where 1=1

		and t.open_time >= #{createTimeBegin}


		and t.open_time <= #{createTimeEnd}

	LIMIT #{start}, #{numPerPage}
	;

  	select t.*,
  	case when t.product_type='REG' then
  		case when e.return_status = 'SUCCESS' then DATE_FORMAT(e.update_time,'%Y%m%d%H%i%s') else '' end
  	else case when f.status = 'RETURNED' then DATE_FORMAT(f.update_time,'%Y%m%d%H%i%s') else '' end
  	end  real_date
from (
  	select b.product_type,b.id,a.mobile user_account, d.id product_code, d.`name` product_name, 1 as total_period,
		DATE_FORMAT(b.last_finish_interest_date,'%Y%m%d%H%i%s') profit_begin_time, b.id as period,
		DATE_FORMAT(b.last_finish_interest_date,'%Y%m%d%H%i%s') plan_date,
		b.open_balance*100 principal_amount, 
		case when d.term <0 then cast(b.open_balance*d.base_rate/365*ABS(d.term) as decimal(18,0)) 
		when d.term = 12 then cast(b.open_balance*d.base_rate as decimal(18,0)) 
		ELSE cast(b.open_balance*d.base_rate/365*d.term*30 as decimal(18,0)) end profit_amount,
		case when b.`status` = 5 then 'FINISH' else 'WAITING' end jet_plan_status
	from bs_user a,bs_account c,bs_product d,
		bs_sub_account b
		where a.id = c.user_id and c.id = b.account_id and b.product_id = d.id
		and a.agent_id = #{agentId} 

			and b.product_id = #{productId} 


			and a.mobile = #{userMobile} 

		and b.product_type in ('REG', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7') and b.`status` in (2,5,7) 

		and b.open_time >= #{createTimeBegin}


		and b.open_time <= #{createTimeEnd}

	ORDER BY b.open_time desc LIMIT #{start}, #{numPerPage}
	)t LEFT JOIN bs_batch_buy_detail e on t.id = e.sub_account_id
		LEFT JOIN bs_deposition_quit_apply f on t.id = f.sub_account_id;

  	select count(b.id)
	from bs_user a,bs_account c,bs_product d,
		bs_sub_account b 
		where a.id = c.user_id and c.id = b.account_id and b.product_id = d.id
		and a.agent_id = #{agentId} 

			and b.product_id = #{productId} 


			and a.mobile = #{userMobile} 


		and b.product_type in ('REG', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7') and b.`status` in (2,5,7) 

		and b.open_time >= #{createTimeBegin}


		and b.open_time <= #{createTimeEnd}

SELECT COUNT(c.id) FROM bs_sub_account a, bs_account b, bs_user c WHERE a.account_id = b.id AND b.user_id = c.id AND a.status = '1' AND a.product_type = 'DEP_JSH' AND c.agent_id = 15
AND c.mobile IN
#{userAccount}
AND c.register_time >= #{createTimeBegin}
AND c.register_time <= #{createTimeEnd}

SELECT c.mobile AS user_account, a.balance*100 AS total_balance, a.available_balance*100 AS current_balance, a.freeze_balance*100 AS frozen_balance FROM bs_sub_account a, bs_account b, bs_user c WHERE a.account_id = b.id AND b.user_id = c.id AND a.status = '1' AND a.product_type = 'DEP_JSH' AND c.agent_id = 15
AND c.mobile IN
#{userAccount}
AND c.register_time >= #{createTimeBegin}
AND c.register_time <= #{createTimeEnd}
ORDER BY c.register_time DESC LIMIT #{start}, #{numPerPage}

select DISTINCT * from (select a.id,a.account_id,a.product_id,a.product_type, a.open_balance,a.balance,a.available_balance,a.freeze_balance, a.`status`,a.interest_begin_date, bu.id user_id,bu.agent_id,hfu.hf_user_id, CASE WHEN sred.available_balance IS NULL THEN 0 ELSE sred.available_balance END red_available_balance, sred.id red_sub_account_id, CASE WHEN sred.available_balance IS NULL THEN a.available_balance ELSE sred.available_balance+a.available_balance END sum_available_balance from bs_account ba, bs_user bu, bs_hfbank_user_ext hfu, bs_sub_account a LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id and b.`status` IN ('INIT', 'PASS','PROCESS') LEFT JOIN bs_sub_account_pair sp on sp.auth_account_id = a.id LEFT JOIN bs_sub_account sred on sred.id = sp.red_account_id and sred.product_type='RED_ZSD' where a.account_id = ba.id and ba.user_id =bu.id and a.product_type=#{productType} and b.id is null and a.interest_begin_date <= DATE(NOW()) and a.`status`=2 and hfu.user_id = bu.id
and ba.user_id not in
#{userId}
)t where 1=1
and sum_available_balance>=#{minBalance}
and sum_available_balance>0
and sum_available_balance<#{maxBalance}
ORDER BY sum_available_balance asc
ORDER BY interest_begin_date asc,sum_available_balance desc

select a.id,a.account_id,a.product_id,a.product_type, a.open_balance,a.balance,a.available_balance,a.freeze_balance, a.`status`,a.interest_begin_date, bu.id user_id,bu.agent_id,hfu.hf_user_id from bs_account ba, bs_user bu, bs_hfbank_user_ext hfu, bs_sub_account a LEFT JOIN bs_deposition_quit_apply b ON a.id = b.sub_account_id and b.`status` IN ('INIT', 'PASS','PROCESS') LEFT JOIN bs_sub_account_pair sp on sp.auth_account_id = a.id where a.account_id = ba.id and ba.user_id =bu.id and a.product_type=#{productType} and b.id is null and a.interest_begin_date <= DATE(NOW()) and a.`status`=2 and a.available_balance>0 and hfu.user_id = bu.id
and ba.user_id in
#{userId}

select a.amount + b.amount from (
  select ifnull(sum(a.approve_amount), 0) as amount from ln_loan a where a.`status` = 'PAIED'
) as a, (
  select ROUND(sum(open_balance), 0) as amount from bs_sub_account where product_type='REG'
  and `status`=5 and last_finish_interest_date <='2018-01-11'
) as b

select a.amount + b.amount from (
  select ifnull(sum(a.approve_amount), 0) as amount from ln_loan a where a.`status` = 'PAIED'
) as a, (
  select ROUND(sum(open_balance), 0) as amount from bs_sub_account where product_type='REG'
  and `status`=5 and last_finish_interest_date <='2018-01-11'
) as b

select count(distinct b.user_id) from bs_sub_account a,bs_account b where a.`status` in(2,5,7) and a.account_id = b.id and a.product_type not in('DEP_JSH','JSH','JLJ')
and b.user_id not in
#{userId}

select count(distinct a.bs_user_id) from ln_loan_relation a, bs_sub_account b where a.`status` = 'SUCCESS' and a.left_amount != 0 and a.total_amount != 0 and a.bs_sub_account_id = b.id and b.`status` in (2, 7)
and a.bs_user_id not in
#{userId}

select ifnull(sum(a.left_amount),0) as left_amount from (
    select a.* from (
        select ifnull(sum(a.left_amount),0) as left_amount, a.bs_user_id from ln_loan_relation a where a.`status` = 'SUCCESS' and a.left_amount != 0 and a.total_amount != 0

            and a.bs_user_id not in

                #{userId}


        group by a.bs_user_id
    ) as a order by a.left_amount desc limit 10
) as a

select ifnull(a.left_amount,0) from (
    select ifnull(sum(a.left_amount),0) as left_amount, a.bs_user_id from ln_loan_relation a where a.`status` = 'SUCCESS' and a.left_amount != 0 and a.total_amount != 0

        and a.bs_user_id not in

            #{userId}


    group by a.bs_user_id
) as a order by a.left_amount desc limit 1

    	SELECT ifnull(SUM(a.open_balance),0) as invest_amount FROM bs_sub_account a 
		WHERE a.`status` NOT IN (1,6) AND a.product_type in ('REG', 'AUTH', 'AUTH_YUN', 'AUTH_ZSD', 'RED', 'RED_ZSD', 'AUTH_7', 'RED_7', 'AUTH_FREE', 'RED_FREE') 
		AND year(a.open_time) = #{year} and MONTH(a.open_time) = #{month};

    	SELECT count(DISTINCT(a.account_id)) FROM bs_sub_account a 
		WHERE a.`status` NOT IN (1,6) AND a.product_type in ('REG', 'AUTH', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7', 'AUTH_FREE') 
		AND year(a.open_time) = #{year} and MONTH(a.open_time) = #{month} ;

    	SELECT count(a.id) FROM bs_sub_account a 
		WHERE a.`status` NOT IN (1,6) AND a.product_type in ('REG', 'AUTH', 'AUTH_YUN', 'AUTH_ZSD', 'AUTH_7', 'AUTH_FREE') 
		AND year(a.open_time) = #{year} and MONTH(a.open_time) = #{month};

    	SELECT case when e.term = 12 then 365 else e.term*30 end product_term, 
    		sum(ifnull(e.open_balance,0)+IFNULL(e.red_balance,0))amount FROM
	(
	SELECT d.open_balance red_balance,aaa.* FROM(
		SELECT c.red_account_id, aa.* FROM (
		SELECT b.term, a.* FROM bs_sub_account a, bs_product b 
			WHERE year(a.open_time)=#{year}
				and MONTH(a.open_time)=#{month}
				and a.product_type in ('REG','AUTH','AUTH_YUN','AUTH_ZSD','AUTH_7','AUTH_FREE') 
				AND a.`status` in (2,5,7) and a.product_id=b.id and b.id!=504 ) aa
				LEFT JOIN bs_sub_account_pair c ON c.auth_account_id=aa.id )aaa LEFT JOIN bs_sub_account d ON aaa.red_account_id=d.id
		) e GROUP BY e.term;

    select SUM(sa.open_balance) 
	from bs_sub_account sa where sa.`status` in (2,5,7) 
		and product_type in ('REG','AUTH_7','AUTH_YUN','AUTH_ZSD','AUTH','RED','RED_ZSD','RED_7','AUTH_FREE','RED_FREE') 
			GROUP BY DATE(sa.open_time) 
		ORDER BY SUM(sa.open_balance) desc limit 1;
