mapper.select:
select

from ln_loan
where id = #{id,jdbcType=INTEGER}

  	select d.id_card ln_user_id_card, d.mobile ln_user_ZANAccount,
		d.user_name ln_userN_name, a.id, a.partner_loan_Id partner_loan_id,
 		a.loan_time, a.period,a.purpose, a.approve_amount, a.agreement_rate
	from ln_loan a, ln_user d
	where a.ln_user_id = d.id  and a.partner_loan_id = #{partnerLoanId}
		and d.partner_code = #{partnerCode}

SELECT COUNT(*) FROM ln_loan_relation a LEFT JOIN ln_loan b ON a.loan_id = b.id LEFT JOIN bs_user c ON a.bs_user_id = c.id LEFT JOIN bs_agent d ON c.agent_id = d.id WHERE b.`status` = 'PAIED' AND a.id not in( select DISTINCT(in_loan_relation_id) from ln_credit_transfer )
AND b.loan_time >= #{loanTimeStart}
AND b.loan_time <= DATE_FORMAT(#{loanTimeEnd},'%Y-%m-%d 23:59:59')

SELECT COUNT(*) FROM ln_loan_relation r LEFT JOIN bs_user u ON r.bs_user_id=u.id LEFT JOIN bs_agent a ON a.id=u.agent_id LEFT JOIN ln_loan l ON l.id=r.loan_id WHERE a.id=15 AND l.`status`='PAIED' AND r.id NOT IN (select t.in_loan_relation_id FROM ln_credit_transfer t)
AND l.loan_time >= #{loanTimeStart}
AND l.loan_time <= DATE_FORMAT(#{loanTimeEnd},'%Y-%m-%d 23:59:59')

SELECT COUNT(*) FROM ln_loan_relation a LEFT JOIN ln_loan b ON a.loan_id = b.id LEFT JOIN bs_user c ON a.bs_user_id = c.id LEFT JOIN bs_agent d ON c.agent_id = d.id WHERE b.`status` = 'PAIED' AND c.agent_id = 15 AND a.id not in( select DISTINCT(in_loan_relation_id) from ln_credit_transfer )
AND b.loan_time >= #{loanTimeStart}
AND b.loan_time <= DATE_FORMAT(#{loanTimeEnd},'%Y-%m-%d 23:59:59')

SELECT t.loanId, t.lnUserId, t.partner, t.loanAmount, n.noReturnAmount, t.totalServiceFee, n.noReturnServiceFee, t.period, n.noReturnPeriod, l.latePeriod, t.applyTime, t.status FROM ( SELECT l.id AS loanId, u.id AS lnUserId, u.partner_code as partner, l.period, l.create_time as applyTime, l.approve_amount as loanAmount, SUM(s.plan_total) - l.approve_amount AS totalServiceFee, l.status FROM ln_loan l LEFT JOIN ln_user u ON u.id = l.ln_user_id LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id
WHERE 1=1
AND l.status=#{status}
AND l.apply_amount >= #{sLoan}
AND l.apply_amount <= #{eLoan}
AND l.id NOT IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED') GROUP BY rs.loan_id ) te WHERE te.num > #{lateEnd} )
AND l.id IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED') GROUP BY rs.loan_id ) te WHERE te.num >= #{lateStart}
AND te.num <= #{lateEnd}
)
AND l.id IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs
WHERE rs.status in ('LATE_NOT','LATE_REPAIED')
GROUP BY rs.loan_id ) te WHERE te.num >= #{lateStart} )
AND l.id NOT IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED')
GROUP BY rs.loan_id ) te WHERE te.num > #{lateEnd} )
GROUP BY s.loan_id ) t LEFT JOIN ( SELECT l.id, SUM(s.plan_total) - SUM(d.plan_amount) AS noReturnServiceFee, SUM(d.plan_amount) AS noReturnAmount, COUNT(s.id) AS noReturnPeriod FROM ln_loan l LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id LEFT JOIN ln_repay_schedule_detail d ON d.plan_id = s.id WHERE s.`status` IN ('INIT', 'LATE_NOT') AND d.subject_code = 'PRINCIPAL' GROUP BY l.id ) n ON n.id = t.loanId LEFT JOIN ( SELECT l.id, COUNT(s.id) AS latePeriod FROM ln_loan l LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id WHERE s.`status` IN ('LATE_NOT', 'LATE_REPAIED') GROUP BY l.id ) l ON l.id = t.loanId
WHERE 1=1
AND t.loanId=#{loanId}
AND t.lnUserId = #{lnUserId}
AND t.applyTime >= #{loanTimeStart}
AND t.applyTime <= DATE_FORMAT(#{loanTimeEnd},'%Y-%m-%d 23:59:59')
AND (n.noReturnAmount >= #{sNoReturn} OR n.noReturnAmount IS NULL )
AND n.noReturnAmount >= #{sNoReturn}
AND n.noReturnAmount IS NULL
AND ( n.noReturnAmount <= #{eNoReturn} OR n.noReturnAmount IS NULL )
AND t.partner = #{partner}
ORDER BY t.applyTime DESC

SELECT COUNT(t.loanId) FROM ( SELECT l.id AS loanId, u.id AS lnUserId, u.partner_code as partner, l.period, l.create_time as applyTime, l.approve_amount as loanAmount, SUM(s.plan_total) - l.approve_amount AS totalServiceFee FROM ln_loan l LEFT JOIN ln_user u ON u.id = l.ln_user_id LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id WHERE 1=1
AND l.status=#{status}
AND l.apply_amount >= #{sLoan}
AND l.apply_amount <= #{eLoan}
AND l.id NOT IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED') GROUP BY rs.loan_id ) te WHERE te.num > #{lateEnd} )
AND l.id IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED') GROUP BY rs.loan_id ) te WHERE te.num >= #{lateStart}
AND te.num <= #{lateEnd}
)
AND l.id IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs
WHERE rs.status in ('LATE_NOT','LATE_REPAIED')
GROUP BY rs.loan_id ) te WHERE te.num >= #{lateStart} )
AND l.id NOT IN ( SELECT loan_id FROM ( SELECT rs.loan_id,COUNT(rs.loan_id) as num FROM ln_repay_schedule rs WHERE rs.status in ('LATE_NOT','LATE_REPAIED')
GROUP BY rs.loan_id ) te WHERE te.num > #{lateEnd} )
GROUP BY s.loan_id ) t LEFT JOIN ( SELECT l.id, SUM(s.plan_total) - SUM(d.plan_amount) AS noReturnServiceFee, SUM(d.plan_amount) AS noReturnAmount, COUNT(s.id) AS noReturnPeriod FROM ln_loan l LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id LEFT JOIN ln_repay_schedule_detail d ON d.plan_id = s.id WHERE s.`status` IN ('INIT', 'LATE_NOT') AND d.subject_code = 'PRINCIPAL' GROUP BY l.id ) n ON n.id = t.loanId LEFT JOIN ( SELECT l.id, COUNT(s.id) AS latePeriod FROM ln_loan l LEFT JOIN ln_repay_schedule s ON l.id = s.loan_id WHERE s.`status` IN ('LATE_NOT', 'LATE_REPAIED') GROUP BY l.id ) l ON l.id = t.loanId
WHERE 1=1
AND t.loanId=#{loanId}
AND t.lnUserId = #{lnUserId}
AND t.applyTime >= #{loanTimeStart}
AND t.applyTime <= DATE_FORMAT(#{loanTimeEnd},'%Y-%m-%d 23:59:59')
AND t.partner = #{partner}
AND (n.noReturnAmount >= #{sNoReturn} OR n.noReturnAmount IS NULL )
AND n.noReturnAmount >= #{sNoReturn}
AND n.noReturnAmount IS NULL
AND ( n.noReturnAmount <= #{eNoReturn} OR n.noReturnAmount IS NULL )
ORDER BY t.applyTime DESC

     	select  sum(approve_amount)as sum_amount, period as term
		from ln_loan
		where 1=1

			and loan_time >=#{beginDate}


			and loan_time <#{endDate}

		GROUP BY period;

select count(a.id) from ln_loan a left join ln_user b on a.ln_user_id = b.id
where b.partner_code = #{channel}
and a.partner_order_no = #{orderNo}

select a.* from ln_loan a left join ln_user b on a.ln_user_id = b.id
where b.partner_code = #{partnerCode}
and a.partner_order_no = #{orderNo}

select * from ln_loan a left join ln_user b on a.ln_user_id = b.id
where b.partner_code = #{channel}
and a.partner_loan_id = #{loanId}

SELECT a.partner_loan_id,a.loan_time as finish_time from ln_loan a
LEFT JOIN ln_user b on a.ln_user_id = b.id
WHERE date(a.loan_time) = #{today}
AND a.status = 'PAIED'
AND b.partner_code = #{partner}

  select IFNULL(SUM(approve_amount),0) from ln_loan 
	where (`status` in ('CHECKED','PAYING') and DATE(create_time) = DATE(NOW()))
	or (`status` = 'PAIED' and DATE(loan_time) = DATE(NOW()));

SELECT d.partner_code, a.partner_loan_id, c.id AS deposition_id, a.create_time, a.loan_time, b.user_name, b.amount, IFNULL(a.head_fee, 0) AS head_fee, a.approve_amount, a.period, b.bank_name, b.id_card, b.bank_card_no, b.order_no, b.status AS order_status, b.return_code, b.return_msg, a.ln_user_id, b.mobile, a.status AS loan_status, a.id AS loan_id, e.partner_repay_id, a.agreement_rate, c.`status` AS depositionStatus, c.update_time AS depositionUpdateTime FROM ln_loan a LEFT JOIN ln_pay_orders b ON a.pay_order_no=b.order_no LEFT JOIN ln_deposition_target c ON a.id=c.loan_id LEFT JOIN ln_user d ON a.ln_user_id = d.id LEFT JOIN (SELECT loan_id, partner_repay_id FROM ln_repay_schedule WHERE partner_repay_id LIKE 'RGCL99900%' AND status = 'REPAIED') e ON a.id = e.loan_id WHERE 1 = 1
AND b.mobile LIKE concat(concat('%', #{mobile,jdbcType=VARCHAR}),'%')
AND b.user_name LIKE concat(concat('%', #{userName,jdbcType=VARCHAR}),'%')
AND b.id_card LIKE concat(concat('%', #{idCard,jdbcType=VARCHAR}),'%')
AND b.order_no LIKE concat(concat('%', #{orderNo,jdbcType=VARCHAR}),'%')
AND a.status = #{loanStatus}
AND a.status IN ('PAYING', 'PAIED', 'CHECKED')
AND a.ln_user_id = #{lnUserId}
AND b.bank_card_no LIKE concat(concat('%', #{bankCardNo,jdbcType=VARCHAR}),'%')
AND c.id = #{depositionId}
AND d.partner_code = #{partnerCode}
AND a.partner_loan_id = #{partnerLoanId}
AND a.create_time >= #{startTime}
AND a.create_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d 23:59:59')
ORDER BY a.loan_time DESC, a.create_time DESC

SELECT
  COUNT(a.id)
FROM ln_loan a LEFT JOIN ln_pay_orders b ON a.pay_order_no=b.order_no
LEFT JOIN ln_deposition_target c ON a.id=c.loan_id
LEFT JOIN ln_user d ON a.ln_user_id = d.id
WHERE 1 = 1

  AND b.mobile LIKE concat(concat('%', #{mobile,jdbcType=VARCHAR}),'%')


  AND b.user_name LIKE concat(concat('%', #{userName,jdbcType=VARCHAR}),'%')


  AND b.id_card LIKE concat(concat('%', #{idCard,jdbcType=VARCHAR}),'%')


  AND b.order_no LIKE concat(concat('%', #{orderNo,jdbcType=VARCHAR}),'%')


  AND a.status = #{loanStatus}


  AND a.status IN ('PAYING', 'PAIED', 'CHECKED')


  AND a.ln_user_id = #{lnUserId}


  AND b.bank_card_no LIKE concat(concat('%', #{bankCardNo,jdbcType=VARCHAR}),'%')


  AND c.id = #{depositionId}


  AND d.partner_code = #{partnerCode}


  AND a.partner_loan_id = #{partnerLoanId}


  AND a.create_time >= #{startTime}


  AND a.create_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d 23:59:59')

SELECT IFNULL(SUM(free_amount),0) + IFNULL(SUM(partner_amount),0) FROM ln_daily_amount
WHERE partner_code = 'YUN_DAI_SELF' AND status = 'AVALIABLE'
AND use_date BETWEEN #{startTime} AND #{endTime}

SELECT IFNULL(SUM(free_amount),0) + IFNULL(SUM(partner_amount),0) FROM ln_daily_amount
WHERE partner_code = '7_DAI_SELF' AND status = 'AVALIABLE'
AND use_date BETWEEN #{startTime} AND #{endTime}

SELECT
  count(*) AS paied_count,
  IFNULL(SUM(a.approve_amount), 0) AS paied_total_amount
FROM ln_loan a, ln_user b
WHERE a.ln_user_id=b.id AND b.partner_code= #{partnerCode}
AND a.status = 'PAIED' AND DATE(a.create_time) BETWEEN #{startTime} AND #{endTime}

SELECT
  count(*) AS paying_count,
  IFNULL(SUM(a.approve_amount), 0) AS paying_total_amount
FROM ln_loan a, ln_user b
WHERE a.ln_user_id=b.id AND b.partner_code= #{partnerCode}
AND a.status IN ('PAYING', 'CHECKED') AND DATE(a.create_time) BETWEEN #{startTime} AND #{endTime}

SELECT (@rowNO := @rowNo + 1) rowno, t.* FROM (SELECT @rowNO := #{position}) b, ( SELECT b.user_name ln_user_name, b.mobile ln_mobile, '' type, a.approve_amount loan_amount, a.head_fee collect_amount, 0.0 pay_amount, a.loan_time gen_time FROM ln_loan a LEFT JOIN ln_user b ON a.ln_user_id = b.id WHERE a.`status`='PAIED'
AND b.partner_code = 'YUN_DAI_SELF'
AND b.partner_code = 'ZSD'
AND (b.partner_code = 'ZSD' OR b.partner_code = 'YUN_DAI_SELF')
UNION ALL SELECT '' ln_user_name, '' ln_mobile, '' type, 0.0 loan_amount, 0.0 collect_amount, a.amount pay_amount, a.update_time gen_time FROM bs_pay_orders a WHERE a.channel_trans_type = 'TRANSFER' AND a.status='6'
AND a.trans_type = 'HFBANK_YUN_HEAD_FEE_TRANSFER'
AND a.trans_type = 'TRANS_HFBANK_ZSD_HEAD_FEE_TRANSFER'
AND (a.trans_type = 'HFBANK_YUN_HEAD_FEE_TRANSFER' OR a.trans_type = 'TRANS_HFBANK_ZSD_HEAD_FEE_TRANSFER')
) t WHERE 1=1
AND t.ln_user_name like concat(concat('%', #{userName}),'%')
AND t.ln_mobile like concat(concat('%', #{mobile}),'%')
AND t.type = #{type}
AND t.gen_time >= #{startTime}
AND t.gen_time <= #{endTime}
LIMIT #{position}, #{offset}
