select a.id, a.loan_id, a.bs_user_id, a.bs_sub_account_id, a.ln_user_id, a.ln_sub_account_id, a.init_amount, a.discount_amount, a.total_amount, CASE WHEN fin.paying_principal >0 THEN a.left_amount -fin.paying_principal ELSE a.left_amount END as left_amount, a.first_term, a.`status`, a.trans_mark, a.bid_status, a.create_time, a.update_time, b.interest_begin_date,b.last_finish_interest_date, b.product_rate base_rate, CASE WHEN a.trans_mark ='NORMAL' THEN DATE(d.loan_time) ELSE DATE(a.create_time) END AS relation_begin_date, CASE WHEN a.trans_mark = 'NORMAL' THEN 0 ELSE f.in_amount-f.amount END last_pay_interest, e.plan_date last_pay_interest_date,hfu.hf_user_id,d.agreement_rate from ln_loan_relation a LEFT JOIN ln_credit_transfer f on f.in_loan_relation_id = a.id LEFT JOIN ( SELECT * FROM ( select b1.*,c1.plan_amount principal,d1.plan_amount interest from ln_loan_relation a1, ln_repay_schedule b1, ln_repay_schedule_detail c1, ln_repay_schedule_detail d1 where a1.loan_id = b1.loan_id and b1.id = c1.plan_id and b1.id = d1.plan_id and b1.`status` in('PART_REPAY','REPAIED','LATE_NOT','LATE_REPAIED') and b1.finish_time is not null
and a1.bs_sub_account_id = #{bsSubAccountId}
and a1.loan_id = #{loanId}
and c1.subject_code='PRINCIPAL' and d1.subject_code = 'INTEREST' and c1.plan_amount = 0 ORDER BY b1.plan_date desc ) aa GROUP BY aa.loan_id )e on e.loan_id = a.loan_id LEFT JOIN (SELECT sum(plan_principal) paying_principal ,relation_id from ln_finance_repay_schedule where `status` = 'REPAYING' GROUP BY relation_id) fin ON fin.relation_id = a.id, bs_sub_account b,bs_product c,bs_hfbank_user_ext hfu,ln_loan d where a.bs_sub_account_id = b.id and hfu.user_id = a.bs_user_id and b.product_id = c.id and a.loan_id = d.id and a.left_amount >0 and a.`status`='SUCCESS'
and a.bs_sub_account_id = #{bsSubAccountId}
and a.loan_id = #{loanId} ORDER BY left_amount DESC
;

