SELECT SUM(ba.left_amount * TIMESTAMPDIFF(DAY,ba.bill_date,ba.finish_date)*ba.agreement_rate/36500) quit_interest_balance FROM( SELECT bd.*, CASE WHEN f.id IS NOT NULL THEN date_add(f.plan_date , interval 1 day) WHEN e.id IS NULL THEN DATE(loan_time) ELSE e.plan_date END bill_date FROM (
SELECT aa.last_finish_interest_date finish_date, g.agreement_rate, d.left_amount, g.id loan_id, g.loan_time FROM (SELECT a.id,a.last_finish_interest_date FROM bs_sub_account a WHERE a.product_type IN ('AUTH_YUN' ) AND a.`status` IN ('2','7') AND DATE(a.last_finish_interest_date) = DATE(#{finishDate}) ) aa, ln_loan_relation d , ln_loan g WHERE d.bs_sub_account_id = aa.id AND d.loan_id = g.id AND d.`status`='SUCCESS' AND !FIND_IN_SET(d.bs_user_id,(SELECT conf_value FROM bs_sys_config WHERE conf_key = 'YUN_DAI_SELF_SUPER_FINANCE_USER_ID'))
) bd LEFT JOIN (select id,loan_id,plan_date from ln_repay_schedule where `status` = 'INIT' and serial_id = 1 ) e ON bd.loan_id = e.loan_id LEFT JOIN ( SELECT tt.id,tt.loan_id,tt.plan_date FROM (SELECT id,loan_id,plan_date FROM ln_repay_schedule t WHERE t.`status` IN ('REPAIED','LATE_NOT','PART_REPAY','LATE_REPAIED') ORDER BY t.serial_id DESC )tt GROUP BY tt.loan_id ) f ON bd.loan_id = f.loan_id )ba GROUP BY ba.finish_date

