select a.id, a.loan_id, a.bs_user_id, a.bs_sub_account_id, a.ln_user_id, a.ln_sub_account_id, a.init_amount, a.discount_amount, a.total_amount, CASE WHEN fin.paying_principal >0 THEN a.left_amount -fin.paying_principal ELSE a.left_amount END as left_amount, a.first_term, a.`status`, a.trans_mark, a.bid_status, a.create_time, a.update_time, b.interest_begin_date,b.last_finish_interest_date, b.product_rate base_rate, CASE WHEN a.trans_mark ='NORMAL' THEN DATE(d.loan_time) ELSE DATE(a.create_time) END AS relation_begin_date, CASE WHEN a.trans_mark = 'NORMAL' THEN 0 ELSE f.in_amount-f.amount END last_pay_interest, e.plan_date last_repay_plan_date,hfu.hf_user_id,d.agreement_rate from ( select * from ln_loan_relation where left_amount >0
and loan_id = #{loanId}
)a LEFT JOIN ln_credit_transfer f on f.in_loan_relation_id = a.id LEFT JOIN ( select * from( select * from ln_repay_schedule where `status` in('LATE_NOT','LATE_REPAIED','REPAIED')
and loan_id = #{loanId}
)b ORDER BY b.plan_date desc limit 1 )e on e.loan_id = a.loan_id LEFT JOIN (SELECT sum(plan_principal) paying_principal ,relation_id from ln_finance_repay_schedule where `status` = 'REPAYING' GROUP BY relation_id) fin ON fin.relation_id = a.id, bs_sub_account b,bs_product c,bs_hfbank_user_ext hfu,ln_loan d where a.bs_sub_account_id = b.id and hfu.user_id = a.bs_user_id and b.product_id = c.id and a.loan_id = d.id and a.left_amount >0 and a.`status`='SUCCESS' ORDER BY left_amount DESC

        select IFNULL(sum(a.left_amount), 0) from ln_loan_relation a, ln_user u

        	a.ln_user_id = u.id AND a.`status` = 'SUCCESS' AND a.left_amount > 0

				AND u.partner_code = #{partnerCode}



					AND a.create_time <= #{endTime}

        select IFNULL(sum(a.left_amount), 0) from ln_loan_relation a

        	a.`status` = 'SUCCESS' AND a.left_amount > 0


					AND a.create_time <= #{endTime}
